    "match": {
      "request": {
          "methods": [
          "GET",
          "POST"
          ],
          "schemes": [
          "HTTP",
          "HTTPS"
          ],
          "url": "*.example/api_test*"
      },
      "response": {
          "status": [
          401,
          403
          ],
          "origin_traffic": true
      }
    },
    "threshold": 60,
    "period": 900,
    "action": {
      "mode": "simulate",
      "timeout": 86400,
      "response": {
          "content_type": "text/xml",
          "body": "<error>This request has been rate-limited.</error>"
      },

    "bypass":[
      {
        "name":"url","value":"api.example.com/*"
      }
    ],
    "correlate":{ "by": "nat" },
    "disabled": false,
    "description": "Prevent multiple login failures to mitigate brute force attacks"
    }
}

